use mysql_binlog_parse::instance::log_buffer::LogBuffer;

#[test]
fn i32to_float32tet() {
    let mut result = LogBuffer::from(Box::from([1, 1, 1, 1, 1, 1, 1, 1]), 0, 8).unwrap();
    assert_eq!(0.000000000000000000000000000000000000023694278,  result.get_float32_pos(0));
    assert_eq!(0.000000000000000000000000000000000000023694278,  result.get_float32());
}

#[test]
fn i64to_float64tet() {
    let mut result = LogBuffer::from(Box::from([1, 1, 1, 1, 1, 1, 1, 1]), 0, 8).unwrap();
    assert_eq!(0.,  result.get_double64_pos(0));
    assert_eq!(0.,  result.get_double64());
}

